    function emitMetrics(A, endpoint) {
        var $ = A.$;
        let dataParams = { 'asin': "B00G36GT5A" };
        // Emit clickstream metrics on page load succeed
        let anti_csrf = document.querySelector('meta[name="csrf-token"]') === null ?
                                                  'faketoken': document.querySelector('meta[name="csrf-token"]')
                                                  .getAttribute('content');
        A.post(endpoint, {
            headers: {"anti-csrftoken-a2z": anti_csrf},
            params: dataParams,
            success: function (response) {
                if (response === true) {
                    console.log('Client page load success');
                } else {
                    console.log('Client page load failed');
                }
            },
            error: function () {
                console.log('Client page load unknown error');
            }
        });
    }













